import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

export const TopicQuiz = () => {
  const userId = useSelector((state) => state.mernQuize.userId);
  const userName = useSelector((state) => state.mernQuize.userName);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-4xl font-bold text-blue-600 mb-4">Welcome to Quiz App</h1>
      {userId ? (
        <p className="text-lg text-gray-700 mb-6">Hello, {userName}! Ready to test your skills?</p>
      ) : (
        <p className="text-lg text-gray-700 mb-6">Sign in to start taking quizzes!</p>
      )}

      <div className="flex gap-4">
        {userId ? (
          <>
            <Link to="/quiz" className="px-6 py-3 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-700">
              Start Quiz
            </Link>
            <Link to="/leaderboard" className="px-6 py-3 bg-green-500 text-white rounded-lg shadow-md hover:bg-green-700">
              Leaderboard
            </Link>
          </>
        ) : (
          <Link to="/login" className="px-6 py-3 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-700">
            Login
          </Link>
        )}
      </div>
    </div>
  );
};
