import axios from "axios";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { postQuizObj } from "../../Redux/action.js";

export const QuizForm = () => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", options: ["", "", "", ""], correctAnswer: "" }
  ]);

  const handleQuestionChange = (index, field, value) => {
    setQuestions((prev) =>
      prev.map((q, i) =>
        i === index ? { ...q, [field]: value } : q
      )
    );
  };

  const handleOptionChange = (qIndex, optIndex, value) => {
    setQuestions((prev) =>
      prev.map((q, i) =>
        i === qIndex
          ? { ...q, options: q.options.map((opt, j) => (j === optIndex ? value : opt)) }
          : q
      )
    );
  };

  const addQuestion = () => {
    setQuestions([...questions, { question: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const quizData = { title, questionArray: questions };
    dispatch(postQuizObj(quizData));
  };

  return (
    <div className="w-10/12 flex text-slate-50">
      <div className="w-1/2 mt-24 ml-32">
        <img className="h-80 pl-36 mt-8" src="./feedback.gif" alt="feedback" />
      </div>
      <div>
        <h1 className="text-2xl font-bold mb-4">Add Quiz</h1>
        <form onSubmit={handleSubmit}>
          <label className="block font-bold">Title</label>
          <input className="w-full border rounded px-4 py-2" type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Quiz Title" required />

          {questions.map((q, qIndex) => (
            <div key={qIndex} className="mt-4 border p-4 rounded">
              <label className="block font-bold">Question {qIndex + 1}</label>
              <input className="w-full border rounded px-4 py-2 mb-2" type="text" value={q.question} onChange={(e) => handleQuestionChange(qIndex, "question", e.target.value)} placeholder="Enter Question" required />

              <label className="block font-bold">Options</label>
              {q.options.map((opt, optIndex) => (
                <input key={optIndex} className="w-full border rounded px-4 py-2 mb-1" type="text" value={opt} onChange={(e) => handleOptionChange(qIndex, optIndex, e.target.value)} placeholder={`Option ${optIndex + 1}`} required />
              ))}

              <label className="block font-bold">Correct Answer</label>
              <input className="w-full border rounded px-4 py-2" type="text" value={q.correctAnswer} onChange={(e) => handleQuestionChange(qIndex, "correctAnswer", e.target.value)} placeholder="Enter Correct Answer" required />
            </div>
          ))}

          <button type="button" className="bg-green-500 text-white py-2 px-4 rounded mt-4" onClick={addQuestion}>Add Another Question</button>
          <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded mt-4 ml-4">Submit Quiz</button>
        </form>
      </div>
    </div>
  );
};
