import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const UserdetailForAdmin = () => {
  const [users, setUsers] = useState([]); // State to store users

  // Fetch all users from the backend
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:4000/getuser", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUsers(response.data); // Set users in state
      } catch (error) {
        console.error("Error fetching users:", error);
        toast.error("Unable to fetch users");
      }
    };

    fetchUsers();
  }, []);

  // Handle user promotion to admin
  const handlePromoteToAdmin = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.patch(
        `http://localhost:4000/admin/user/${id}/promote`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const updatedUser = response.data.user;

      // Update the local state to reflect the promoted user
      setUsers((prevUsers) =>
        prevUsers.map((user) => (user._id === updatedUser._id ? updatedUser : user))
      );

      toast.success("User promoted to Admin successfully");
    } catch (error) {
      console.error("Error promoting user:", error.response?.data || error);
      toast.error("Unable to promote the User");
    }
  };

  return (
    <div className="w-full max-w-5xl mx-auto p-4">
      {/* Header */}
      <div className="bg-teal-500 rounded-lg w-56 text-center text-white text-2xl font-bold p-4 mx-auto">
        <h1>List of Users</h1>
      </div>

      {/* User List */}
      <div className="flex flex-col container w-full max-w-lg md:max-w-2xl lg:max-w-4xl bg-teal-500 rounded-lg mt-6 mx-auto p-4">
        <ul className="flex flex-col divide-y w-full">
          {users.map((user) => (
            <li
              key={user._id}
              className="flex flex-row items-center p-4 bg-white rounded-lg shadow-md my-2"
            >
              {/* Profile Image */}
              <div className="flex-shrink-0 w-12 h-12 mr-4">
                <img
                  src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ8czzbrLzXJ9R_uhKyMiwj1iGxKhJtH7pwlQ&usqp=CAU"
                  alt="profile"
                  className="object-cover rounded-full h-full w-full"
                />
              </div>

              {/* User Details */}
              <div className="flex-1">
                <h2 className="font-medium text-gray-800">{user.name}</h2>
                <p className="text-gray-600 text-sm">{user.email}</p>
                <p className="text-gray-500 text-xs">
                  Role: {user.role === "admin" ? "Admin" : "User"}
                </p>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-4">
                {/* Promote to Admin Button */}
                {user.role !== "admin" && (
                  <button
                    onClick={() => handlePromoteToAdmin(user._id)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    Promote to Admin
                  </button>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>

      <ToastContainer />
    </div>
  );
};
