import React from "react";
import { Link, useNavigate } from "react-router-dom";
    import {jwtDecode} from "jwt-decode";

import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const Navbarnew = () => {
  const navigate = useNavigate();

  // Check if the token exists in localStorage
  const token = localStorage.getItem("token");
  let userName = null;
  let userRole = null;

  if (token) {
    try {
      // Decode the token to extract user information
      const decodedToken = jwtDecode(token);
      userName = decodedToken.name; // Assuming the token contains the user's name
      userRole = decodedToken.role; // Assuming the token contains the user's role
    } catch (error) {
      console.error("Invalid token:", error);
    }
  }

  const logouthandler = () => {
    // Remove the token from localStorage
    localStorage.removeItem("token");

    // Show a success toast message
    toast.success("Successfully logged out");

    // Redirect to the homepage
    navigate("/");
  };

  return (
    <nav className="w-full bg-gray-900 text-white py-4 px-6 flex justify-between items-center shadow-md">
      {/* App Name */}
      <Link to="/" className="text-2xl font-bold tracking-wide text-yellow-400">
        QuizMaster<span className="text-blue-400"> Pro</span>
      </Link>

      {/* Navigation Options */}
      <div className="flex space-x-6 text-lg ml-auto pl-4 lg:pl-10">
        {token ? (
          <>
            <button
              onClick={() => navigate("/profile")}
              className="hover:text-yellow-300 transition duration-300"
            >
              {userRole === "admin" ? `Admin: ${userName}` : `User: ${userName}`}
            </button>
            <button
              onClick={logouthandler}
              className="hover:text-red-400 transition duration-300"
            >
              Logout
            </button>
          </>
        ) : (
          <Link
            to="/register"
            className="hover:text-green-300 transition duration-300 font-medium"
          >
            Sign In
          </Link>
        )}
      </div>

      <ToastContainer />
    </nav>
  );
};
