import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getQuiz } from "../../Redux/action.js";
import { Quiz } from "./Quiz";

export const Quizes = () => {
  const singleQuiz = useSelector((state) => state?.mernQuize?.QuizData) || [];
  const params = useParams();
  const dispatch = useDispatch();
  const [isLoading, setLoading] = useState(true);

  console.log("Fetching Quiz Data for:", params); // Debugging
  console.log("Redux State - Quiz Data:", singleQuiz); // Debugging

  useEffect(() => {
    dispatch(getQuiz(params));
  }, [dispatch, params]);

  // ✅ Only stop loading when data is received
  useEffect(() => {
    if (singleQuiz.length > 0) {
      setLoading(false);
    }
  }, [singleQuiz]);

  const questionArr = singleQuiz?.[0]?.questionArray || [];

  return isLoading || !singleQuiz.length ? (
    <div>
      <h2 className="text-center text-red-500">Loading Quiz...</h2>
      <iframe
        className="w-4/5 h-96 ml-40"
        src="https://embed.lottiefiles.com/animation/9844"
        title="loading-animation"
      ></iframe>
    </div>
  ) : (
    <Quiz questionArr={questionArr} />
  );
};
