import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {jwtDecode} from "jwt-decode";

export const Profile = () => {
  const [userName, setUserName] = useState(null);
  const [email, setEmail] = useState(null);
  const [role, setRole] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [quizHistory, setQuizHistory] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      // If no token, redirect to login
      navigate("/login");
      return;
    }

    try {
      // Decode the token to extract user information
      const decodedToken = jwtDecode(token);

      // Check if the token is expired
      const currentTime = Date.now() / 1000; // Current time in seconds
      if (decodedToken.exp < currentTime) {
        // Token is expired
        localStorage.removeItem("token");
        navigate("/login");
        return;
      }

      // Set user details from the token
      setUserName(decodedToken.name);
      setEmail(decodedToken.email);
      setRole(decodedToken.role);

      // Fetch quiz history (mock data for now)
      setQuizHistory([
        { quizName: "General Knowledge", score: "8/10", date: "March 25, 2025" },
        { quizName: "Science", score: "7/10", date: "March 20, 2025" },
      ]);
    } catch (error) {
      console.error("Invalid token:", error);
      localStorage.removeItem("token");
      navigate("/login");
    }
  }, [navigate]);

  // Toggle dark mode
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleDeleteAccount = () => {
    if (window.confirm("Are you sure you want to delete your account? This action cannot be undone.")) {
      // Call delete account API (mocked for now)
      console.log("Account deleted");
      localStorage.removeItem("token");
      navigate("/login");
    }
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good Morning";
    if (hour < 18) return "Good Afternoon";
    return "Good Evening";
  };

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 dark:text-white flex items-center justify-center">
      <div className="w-full max-w-5xl mx-auto shadow-2xl rounded-lg p-6">
        {/* Personalized Greeting */}
        <h1 className="text-2xl font-bold text-sky-600 dark:text-sky-400">
          {getGreeting()}, {userName || "Guest"}! ðŸ‘‹
        </h1>

        {/* Motivational Quote */}
        <h1 className="text-teal-500 text-2xl font-extrabold italic mt-6 md:text-3xl">
          Sweat more in practice, bleed less in war.
        </h1>
        <p className="text-gray-700 dark:text-gray-300 font-bold mt-2 text-sm md:text-base">
          â€“ Spartan Warrior Credo
        </p>

        {/* Profile Section */}
        <div className="flex flex-col md:flex-row items-center justify-center mt-10">
          <img src="./profile.gif" alt="Profile" className="w-40 md:w-64 h-auto rounded-full" />
          <div className="mt-6 md:mt-0 md:ml-8">
            <h2 className="text-xl font-bold">Name: {userName || "Guest"}</h2>
            <p className="text-lg">Email: {email || "N/A"}</p>
            <p className="text-lg">Role: {role || "User"}</p>
          </div>
        </div>

        {/* Upload Profile Picture */}
        <div className="mt-6">
          <label className="block text-gray-700 dark:text-gray-300 font-bold mb-2">Update Profile Picture:</label>
          <input
            type="file"
            accept="image/*"
            className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-teal-50 file:text-teal-700 hover:file:bg-teal-100"
          />
        </div>

        {/* Quiz History */}
        <div className="mt-10">
          <h2 className="text-xl font-bold text-gray-800 dark:text-gray-200">Quiz History</h2>
          <ul className="mt-4">
            {quizHistory.map((quiz, index) => (
              <li key={index} className="text-gray-700 dark:text-gray-300">
                <strong>Quiz:</strong> {quiz.quizName} | <strong>Score:</strong> {quiz.score} | <strong>Date:</strong> {quiz.date}
              </li>
            ))}
          </ul>
        </div>

        {/* Buttons Section */}
        <div className="mt-10 flex flex-col md:flex-row items-center justify-center space-y-4 md:space-y-0 md:space-x-4">
          {/* Attempt Quiz Button */}
          <Link to="/categories">
            <button className="bg-teal-500 text-white text-lg font-bold py-2 px-6 rounded-lg shadow-md hover:bg-teal-600 transition">
              Attempt Quiz
            </button>
          </Link>

          {/* Edit Profile Button */}
          <button className="bg-blue-500 text-white text-lg font-bold py-2 px-6 rounded-lg shadow-md hover:bg-blue-600 transition">
            Edit Profile
          </button>

          {/* Change Password Button */}
          <button className="bg-yellow-500 text-white text-lg font-bold py-2 px-6 rounded-lg shadow-md hover:bg-yellow-600 transition">
            Change Password
          </button>

          {/* Logout Button */}
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white text-lg font-bold py-2 px-6 rounded-lg shadow-md hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>

        {/* Dark Mode Toggle */}
        <div className="mt-6">
          <label className="flex items-center">
            <input
              type="checkbox"
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
              className="mr-2"
            />
            Enable Dark Mode
          </label>
        </div>

        {/* Delete Account */}
        <div className="mt-6">
          <button
            onClick={handleDeleteAccount}
            className="bg-red-500 text-white text-lg font-bold py-2 px-6 rounded-lg shadow-md hover:bg-red-600 transition"
          >
            Delete Account
          </button>
        </div>
      </div>
    </div>
  );
};
