import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

export const Profile = () => {
  const [userName, setUserName] = useState(null);
  const [email, setEmail] = useState(null);
  const [role, setRole] = useState(null);
  const [quizHistory, setQuizHistory] = useState([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      navigate("/login");
      return;
    }

    try {
      const decodedToken = jwtDecode(token);
      const currentTime = Date.now() / 1000;

      if (decodedToken.exp < currentTime) {
        localStorage.removeItem("token");
        navigate("/login");
        return;
      }

      setUserName(decodedToken.name);
      setEmail(decodedToken.email);
      setRole(decodedToken.role);

      setQuizHistory([
        { quizName: "General Knowledge", score: "8/10", date: "March 25, 2025" },
        { quizName: "Science", score: "7/10", date: "March 20, 2025" },
      ]);
    } catch (error) {
      console.error("Invalid token:", error);
      localStorage.removeItem("token");
      navigate("/login");
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleDeleteAccount = () => {
    if (window.confirm("Are you sure you want to delete your account?")) {
      console.log("Account deleted");
      localStorage.removeItem("token");
      navigate("/login");
    }
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good Morning";
    if (hour < 18) return "Good Afternoon";
    return "Good Evening";
  };

  return (
    <div className="min-h-screen flex bg-gray-100">
      {/* Mobile Sidebar Toggle Button */}
      <button
        onClick={() => setIsSidebarOpen(true)}
        className="fixed top-4 left-4 md:hidden z-50 p-2 bg-teal-500 text-white rounded"
      >
        ☰ Menu
      </button>

      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 w-64 h-full bg-teal-500 text-white p-4 z-40 transform ${
          isSidebarOpen ? "translate-x-0" : "-translate-x-full"
        } md:translate-x-0 md:w-64 transition-transform duration-300`}
      >
        <button
          onClick={() => setIsSidebarOpen(false)}
          className="md:hidden absolute top-4 right-4 text-white text-2xl"
        >
          ✕
        </button>
        <h2 className="text-2xl font-bold">Menu</h2>
        <ul className="mt-6 space-y-4 overflow-y-auto h-[80vh]">
          <li><Link to="/categories" className="block text-lg hover:text-gray-200">Attempt Quiz</Link></li>
          <li><a href="#quiz-history" className="block text-lg hover:text-gray-200">Quiz History</a></li>
          <li><a href="#profile-details" className="block text-lg hover:text-gray-200">Profile Details</a></li>
          <li><a href="#notifications" className="block text-lg hover:text-gray-200">Notifications</a></li>
          <li>
            <button onClick={handleLogout} className="block text-lg hover:text-gray-200 focus:outline-none">
              Logout
            </button>
          </li>
          <li>
            <button onClick={handleDeleteAccount} className="block text-lg hover:text-gray-200 focus:outline-none">
              Delete Account
            </button>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 ml-0 md:ml-64 p-6">
        <h1 className="text-2xl font-bold text-sky-600">
          {getGreeting()}, {userName || "Guest"}! 👋
        </h1>

        {/* Motivational Quote */}
        <h1 className="text-teal-500 text-2xl font-extrabold italic mt-6 md:text-3xl">
          Sweat more in practice, bleed less in war.
        </h1>
        <p className="text-gray-700 font-bold mt-2 text-sm md:text-base">
          – Spartan Warrior Credo
        </p>

        {/* Profile Section */}
        <div id="profile-details" className="flex flex-col md:flex-row items-center justify-center mt-10">
          <img src="./profile.gif" alt="Profile" className="w-40 md:w-64 h-auto rounded-full" />
          <div className="mt-6 md:mt-0 md:ml-8">
            <h2 className="text-xl font-bold">Name: {userName || "Guest"}</h2>
            <p className="text-lg">Email: {email || "N/A"}</p>
            <p className="text-lg">Role: {role || "User"}</p>
          </div>
        </div>

        {/* Quiz History */}
        <div id="quiz-history" className="mt-10">
          <h2 className="text-xl font-bold text-gray-800">Quiz History</h2>
          <ul className="mt-4">
            {quizHistory.map((quiz, index) => (
              <li key={index} className="text-gray-700">
                <strong>Quiz:</strong> {quiz.quizName} | <strong>Score:</strong> {quiz.score} | <strong>Date:</strong> {quiz.date}
              </li>
            ))}
          </ul>
        </div>

        {/* Notifications */}
        <div id="notifications" className="mt-10">
          <h2 className="text-xl font-bold text-gray-800">Notifications</h2>
          <ul className="mt-4">
            <li className="text-gray-700">📢 New quiz on "History" is now available!</li>
            <li className="text-gray-700">📢 Your quiz results for "Science" have been updated.</li>
          </ul>
        </div>
      </div>
    </div>
  );
};
