import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export const CategorySelection = () => {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get("http://localhost:4000/quiz/category");
        setCategories(response.data);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };
    fetchCategories();
  }, []);

  const handleCategorySelect = (category) => {
    navigate(`/quizzes?category=${category}`);
  };

  return (
    <div className="w-full max-w-xl mx-auto mt-10">
      <h1 className="text-2xl font-bold mb-4">Select a Quiz Category</h1>
      {categories.length > 0 ? (
        <ul>
          {categories.map((category, index) => (
            <li key={index} className="p-4 mb-2 bg-gray-100 rounded-lg cursor-pointer hover:bg-gray-200"
                onClick={() => handleCategorySelect(category)}>
              {category}
            </li>
          ))}
        </ul>
      ) : (
        <p>Loading categories...</p>
      )}
    </div>
  );
};
