import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { deleteUserByAdmin, promoteUserToAdmin, getAllUserDataFromBackend } from "../../Redux/action.js";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const UserdetailForAdmin = () => {
  const dispatch = useDispatch();
  const { users, loading, error } = useSelector((state) => state.userData);

  useEffect(() => {
    dispatch(getAllUserDataFromBackend());
  }, [dispatch]);

  const handleDeleteUser = (id) => {
    if (id) {
      dispatch(deleteUserByAdmin(id));
      toast.success("Successfully Deleted the User");
    } else {
      toast.error("You can't delete the User");
    }
  };

  const handlePromoteToAdmin = (id) => {
    if (id) {
      dispatch(promoteUserToAdmin(id));
      toast.success("User promoted to Admin successfully");
    } else {
      toast.error("Unable to promote the User");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="w-full max-w-5xl mx-auto p-4">
      <div className="bg-teal-500 rounded-lg w-56 text-center text-white text-2xl font-bold p-4 mx-auto">
        <h1>List of Users</h1>
      </div>

      <div className="flex flex-col container w-full max-w-lg md:max-w-2xl lg:max-w-4xl bg-teal-500 rounded-lg mt-6 mx-auto p-4">
        <ul className="flex flex-col divide-y w-full">
          {users.map((user) => (
            <li key={user._id} className="flex flex-row items-center p-4 bg-white rounded-lg shadow-md my-2">
              <div className="flex-shrink-0 w-12 h-12 mr-4">
                <img
                  src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ8czzbrLzXJ9R_uhKyMiwj1iGxKhJtH7pwlQ&usqp=CAU"
                  alt="profile"
                  className="object-cover rounded-full h-full w-full"
                />
              </div>

              <div className="flex-1">
                <h2 className="font-medium text-gray-800">{user.name}</h2>
                <p className="text-gray-600 text-sm">{user.email}</p>
                <p className="text-gray-500 text-xs">Role: {user.role === "admin" ? "Admin" : "User"}</p>
              </div>

              <div className="flex space-x-4">
                {user.role !== "admin" && (
                  <button
                    onClick={() => handlePromoteToAdmin(user._id)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    Promote to Admin
                  </button>
                )}

                <button
                  onClick={() => handleDeleteUser(user._id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Delete User
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>

      <ToastContainer />
    </div>
  );
};
