import axios from "axios";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { postQuizObj } from "../../Redux/action.js";

export const QuizForm = () => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", options: ["", "", "", ""], correctAnswer: "" }
  ]);

  const handleQuestionChange = (index, field, value) => {
    setQuestions((prev) =>
      prev.map((q, i) =>
        i === index ? { ...q, [field]: value } : q
      )
    );
  };

  const handleOptionChange = (qIndex, optIndex, value) => {
    setQuestions((prev) =>
      prev.map((q, i) =>
        i === qIndex
          ? { ...q, options: q.options.map((opt, j) => (j === optIndex ? value : opt)) }
          : q
      )
    );
  };

  const addQuestion = () => {
    setQuestions([...questions, { question: "", options: ["", "", "", ""], correctAnswer: "" }]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const quizData = { title, questionArray: questions };
    dispatch(postQuizObj(quizData));
  };

  return (
    <div className="w-10/12 flex text-black">  {/* Changed text-slate-50 to text-black */}
    <div className="w-1/2 mt-24 ml-32">
      <img className="h-80 pl-36 mt-8" src="./feedback.gif" alt="feedback" />
    </div>

    <div className="">
      <div className="flex text-yellow-500 w-96 font-bold font-serif mb-2 ml-12 mt-14">
        <h1 className="text-2xl text-black">ADD QUESTIONS</h1> {/* Changed text color */}
        <img src="./add.gif" alt="add icon" className="w-1/3 h-20 -mt-6" />
      </div>

      <form className="-mt-6">
        <label className="block uppercase tracking-wide text-xs font-bold mb-2 text-black">
          Title
        </label>
        <input
          className="block w-full bg-gray-200 text-black border rounded py-2 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          type="text"
          placeholder="Title"
          onChange={(event) => setQuiz({ ...quiz, title: event.target.value })}
        />

        <label className="block uppercase tracking-wide text-xs font-bold mb-2 text-black">
          Question
        </label>
        <input
          className="block w-full bg-gray-200 text-black border rounded py-2 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          type="text"
          placeholder="Question"
          onChange={(event) => setQuiz({ ...quiz, questions: event.target.value })}
        />

        <label className="block uppercase tracking-wide text-xs font-bold mb-2 text-black">
          Options
        </label>
        <div>
          {ans?.map((x) => (
            <div key={x.id} className="flex gap-1">
              <input
                className="w-1/2 block bg-gray-200 text-black border rounded py-2 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                type="text"
                placeholder={`Option ${x.id}`}
                name="option"
                value={x.option}
                onChange={(e) => handleType(x.id)(e)}
              />
              <select
                className="form-select appearance-none w-1/2 px-3 h-9 text-black bg-white border border-gray-300 rounded focus:outline-none"
                name="isCorrect"
                value={x.boolean}
                onChange={(e) => handleType(x.id)(e)}
              >
                <option value="">Select the value</option>
                <option value={true}>true</option>
              </select>
            </div>
          ))}
        </div>

        <label className="block uppercase tracking-wide text-xs font-bold mb-2 text-black">
          Answer
        </label>
        <input
          className="block w-full bg-gray-200 text-black border rounded py-2 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          type="text"
          placeholder="Answer"
          onChange={(event) => setQuiz({ ...quiz, correctAnswer: event.target.value })}
        />

        <div className="flex">
          <button
            onClick={handleQuiz}
            className="bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"
          >
            Submit
          </button>
          <button
            onClick={handleUploadnew}
            className="bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded ml-36"
          >
            Upload
          </button>
        </div>
      </form>
    </div>
  </div>

  );
};
